<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN"	"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en"><head>	<meta http-equiv="Content-Type" content="text/html; charset=utf-8"/>	<title>A Simple Modal: Example</title>	<script type="text/javascript" lang="javascript" src="/examples/modal-simple/jquery-1.2.3.js"></script>	<script type="text/javascript" lang="javascript" src="/examples/modal-simple/modal-window.js"></script>		<script type="text/javascript" lang="javascript">	var openMyModal = function(source)	{		modalWindow.windowId = "myModal";		modalWindow.width = 480;		modalWindow.height = 405;		modalWindow.content = "<iframe width='480' height='405' frameborder='0' scrolling='no' allowtransparency='true' src='" + source + "'>&lt/iframe>";		modalWindow.open();	};		</script>	<style type="text/css">	html,body	{		margin:0;		padding:0;	}	.modal-overlay	{		position:fixed;		top:0;		right:0;		bottom:0;		left:0;		height:100%;		width:100%;		margin:0;		padding:0;		background:#fff;		opacity:.75;		filter: alpha(opacity=75);		-moz-opacity: 0.75;		z-index:101;	}	* html .modal-overlay	{   		position: absolute;		height: expression(document.body.scrollHeight > document.body.offsetHeight ? document.body.scrollHeight : document.body.offsetHeight + 'px');	}	.modal-window	{		position:fixed;		top:50%;		left:50%;		margin:0;		padding:0;		z-index:102;	}	* html .modal-window	{		position:absolute;	}	.close-window	{		position:absolute;		width:32px;		height:32px;		right:8px;		top:8px;		background:transparent url('/examples/modal-simple/close-button.png') no-repeat scroll right top;		text-indent:-99999px;		overflow:hidden;		cursor:pointer;		opacity:.5;		filter: alpha(opacity=50);		-moz-opacity: 0.5;	}	.close-window:hover	{		opacity:.99;		filter: alpha(opacity=99);		-moz-opacity: 0.99;	}	</style>	</head><body>	<div style="postion:absolute; top:0; left:0; width:100%; height:25px; background:#000; color:#fff; padding:15px 0 0 0; overlflow:hidden;">	<a style="margin-left:30px;" href="http://deseloper.org">&laquo; Back to Deseloper.org</a> 	</div>	<div style="position:absolute; left:0; top:40px;">		<script type="text/javascript"><!--		google_ad_client = "pub-6411809547640783";		/* Examples - Wide Vertical */		google_ad_slot = "0507563986";		google_ad_width = 160;		google_ad_height = 600;		//-->		</script>		<script type="text/javascript"		src="http://pagead2.googlesyndication.com/pagead/show_ads.js">		</script>	</div>	<div style="margin-left:200px; top:40px;">		<h1>A Simple Modal: Example</h1>		<p>		<a href="/examples/modal-simple/modal.html" onclick="openMyModal('/examples/modal-simple/modal.html'); return false;" class="icoMr">View Window</a>		</p>	</div></body></html>